    private List<TreeEntity> tree;

    public String getSha() {
        return sha;
    }

    public void setSha(String sha) {
        this.sha = sha;
    }

    public String getUrl() {
        return url;
    }

    public void setUrl(String url) {
        this.url = url;
    }

    public boolean isTruncated() {
        return truncated;
    }

    public void setTruncated(boolean truncated) {
        this.truncated = truncated;
    }

    public List<TreeEntity> getTree() {
        return tree;
    }

    public void setTree(List<TreeEntity> tree) {
        this.tree = tree;
    }

    public static class TreeEntity {
        private String path;
        private String mode;
        private String type;//blob or tree
        private String sha;
        private int size;
        private String html_url;

        public String getPath() {
            return path;
        }

        public void setPath(String path) {
            this.path = path;
        }

        public String getMode() {
            return mode;
        }

        public void setMode(String mode) {
            this.mode = mode;
        }

        public String getType() {
            return type;
        }

        @TreeNodeType
        public int getNodeType() {
            if ("blob".equals(type))
                return FILE;
            else return DIR;
        }

        public void setType(String type) {
            this.type = type;
        }

        public String getSha() {
            return sha;
        }

        public void setSha(String sha) {
            this.sha = sha;
        }

        public int getSize() {
            return size;
        }

        public void setSize(int size) {
            this.size = size;
        }

        public String getHtml_url(String user, String repo, String branchName) {
            if (TextUtils.isEmpty(html_url)) {
                html_url = StringUtils.append("https://github.com/", user, "/", repo, "/blob/", branchName, "/", path);
            }
            return html_url;
        }
    }
